DROP TABLE IF EXISTS `drug_resource_0`;
CREATE TABLE `drug_resource_0` (
  `id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '主键',
  `drug_res_type_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT ' 药品和物资类型ID(dw_drug_res_type.id)',
  `base_type` tinyint NOT NULL DEFAULT '0' COMMENT '物品类型：1-药品, 2-耗材, 3-办公用品',
  `status` int DEFAULT '1' COMMENT '药品状态: 0 - 停用, 1 - 使用',
  `type_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '药品或者耗材类别的字典ID(dw_dictionary.id AND dw_dictionary.type_id=[yplb OR hclb])',
  `dict_dosage_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '药品剂型的字典ID(dw_dictionary.id AND dw_dictionary.type_id=ypjx)',
  `code` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '集团编码',
  `pym` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '拼音简码',
  `name` varchar(64) CHARACTER SET utf8 DEFAULT '' COMMENT '药品或者耗材的名称',
  `manufacturer_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '生产厂家的字典ID(dw_dictionary.id AND dw_dictionary.type_id=sccj)',
  `approval_no` varchar(100) CHARACTER SET utf8 DEFAULT NULL COMMENT '批准文号',
  `med_type` int DEFAULT '3' COMMENT '医保分类： 1 - 无自付, 2 - 部分自付, 3 - 全自付',
  `admin_code` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '药监编码',
  `pack_unit_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '包装单位字典ID(dw_dictionary.id AND dw_dictionary.type_id=ypdw)',
  `min_unit_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '最小单位字典ID(dw_dictionary.id AND dw_dictionary.type_id=ypdw)',
  `pack_quantity` int DEFAULT '1' COMMENT '包装含量',
  `dosage_unit_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '剂量单位字典ID(dw_dictionary.id AND dw_dictionary.type_id=jldw)',
  `dosage_quantity` decimal(10,2) DEFAULT '0.00' COMMENT '剂量含量',
  `pack_spec` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '包装规格，生成规则：[剂量含量] [剂量单位] * [包装含量] [最小单位] / [包装单位]',
  `take_method_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '用法(默认用法)(dw_dictionary.id AND dw_dictionary.type_id=ypyf)',
  `take_quantity` decimal(36,4) DEFAULT '0.0000' COMMENT '用量(默认用量)',
  `take_unit_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '用量单位字典ID - 暂留字典',
  `take_unit_name` varchar(36) CHARACTER SET utf8 DEFAULT 'ml' COMMENT '用量单位名称，默认ml',
  `take_frequency` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '频率(默认频率)(dw_dictionary.id AND dw_dictionary.type_id=yppl)',
  `common_unit_type` int DEFAULT '1' COMMENT '常规单位类型: 1 - 包装单位(packUnit), 2 - 最小单位(minUnit)',
  `purchase_price` decimal(8,4) DEFAULT '0.0000' COMMENT '进货价: 单位 元',
  `sale_price` decimal(8,4) DEFAULT '0.0000' COMMENT '基准价: 单位 元',
  `cost_category_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '费用类别字典ID(dw_dictionary.id AND dw_dicitionary.type_id=fylb)',
  `invoice_item_id` varchar(36) CHARACTER SET utf8 DEFAULT '' COMMENT '发票项目字典ID - 暂留',
  `expensive_flag` int DEFAULT '1' COMMENT '贵重物品标识： 0 - 非贵重物品， 1 - 贵重物品',
  `psy_flag_i` int DEFAULT '0' COMMENT ' 精神I类药品标识：0 - 非选中， 1 - 选中',
  `psy_flag_ii` int DEFAULT '0' COMMENT '精神II类药品标识：0 - 非选中，1 - 选中',
  `otc_flag` int DEFAULT '0' COMMENT 'OTC标识：0 - 非选中， 1 - 选中',
  `nar_flag` int DEFAULT '0' COMMENT '麻醉药品标识：0 - 非选中， 1 - 选中',
  `inventory_upper` int DEFAULT '0' COMMENT '库存上限',
  `inventory_lower` int DEFAULT '0' COMMENT '库存下限',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `modify_time` datetime DEFAULT NULL COMMENT '修改时间',
  `del_flag` int DEFAULT '0' COMMENT '删除标志:  0 - 未删除, 1 - 已删除',
  `consumable_type` int NOT NULL DEFAULT '0' COMMENT '耗材类型:1-国产,2-进口',
  `tips` varchar(1024) CHARACTER SET utf8 DEFAULT NULL COMMENT '药品使用注意事项',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ----------------------------
-- Records of drug_resource_0
-- ----------------------------
BEGIN;
COMMIT;

